// AuthContext básico para debug
import { createContext, useContext, useState } from 'react';

console.log('📁 AuthContext.js cargándose...');

const AuthContext = createContext(undefined);

export function AuthProvider({ children }) {
  console.log('🚀 AuthProvider iniciando...');

  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const value = {
    user,
    loading,
    error,
    isAuthenticated: !!user,
    register: async () => ({ success: false, error: 'Función deshabilitada temporalmente' }),
    login: async () => ({ success: false, error: 'Función deshabilitada temporalmente' }),
    logout: async () => {
      setUser(null);
      setError(null);
    }
  };

  console.log('🚀 AuthProvider value:', value);

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
}

export function useAuth() {
  const context = useContext(AuthContext);
  if (context === undefined) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
}

export default AuthContext;
