// src/contexts/AuthContext.js
// Nuevo contexto de autenticaci√≥n que usa el backend Node.js en lugar de Firebase

import { createContext, useContext, useState, useEffect, useCallback } from 'react';
import backendAPI from '../services/backendAPI';

const AuthContext = createContext();

export function AuthProvider({ children }) {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Verificar token al cargar la aplicaci√≥n
  useEffect(() => {
    const initializeAuth = async () => {
      setLoading(true);
      try {
        if (apiClient.isAuthenticated()) {
          // Verificar si el token es v√°lido
          const response = await apiClient.verifyToken();
          if (response.success) {
            setUser(response.user);
          } else {
            // Token inv√°lido, limpiar
            apiClient.clearTokens();
            setUser(null);
          }
        } else {
          setUser(null);
        }
      } catch (error) {
        console.error('Error initializing auth:', error);
        // Si hay error verificando token, limpiar todo
        apiClient.clearTokens();
        setUser(null);
      } finally {
        setLoading(false);
      }
    };

    initializeAuth();
  }, []);

  // Funci√≥n para registrar nuevo usuario
  const register = useCallback(async (userData) => {
    try {
      setError(null);
      setLoading(true);

      const response = await apiClient.register(userData);

      if (response.success) {
        setUser(response.user);
        console.log('‚úÖ Usuario registrado exitosamente:', response.user.username);
        return { success: true, user: response.user };
      } else {
        throw new Error(response.message || 'Error en registro');
      }
    } catch (error) {
      console.error('‚ùå Error en registro:', error);
      setError(error);
      return { 
        success: false, 
        error: error.message || 'Error registrando usuario' 
      };
    } finally {
      setLoading(false);
    }
  }, []);

  // Funci√≥n para login
  const login = useCallback(async (email, password) => {
    try {
      setError(null);
      setLoading(true);

      const response = await apiClient.login(email, password);

      if (response.success) {
        setUser(response.user);
        console.log('‚úÖ Login exitoso:', response.user.username);
        return { success: true, user: response.user };
      } else {
        throw new Error(response.message || 'Error en login');
      }
    } catch (error) {
      console.error('‚ùå Error en login:', error);
      setError(error);
      return { 
        success: false, 
        error: error.message || 'Credenciales incorrectas' 
      };
    } finally {
      setLoading(false);
    }
  }, []);

  // Funci√≥n para logout
  const logout = useCallback(async () => {
    try {
      setLoading(true);
      await apiClient.logout();
      setUser(null);
      console.log('üëã Logout exitoso');
      return { success: true };
    } catch (error) {
      console.error('‚ùå Error en logout:', error);
      // Aunque haya error en logout, limpiar estado local
      setUser(null);
      return { success: false, error: error.message };
    } finally {
      setLoading(false);
    }
  }, []);

  // Funci√≥n para actualizar perfil
  const updateProfile = useCallback(async (profileData) => {
    try {
      setError(null);
      const response = await apiClient.updateProfile(profileData);

      if (response.success) {
        setUser(response.user);
        console.log('‚úÖ Perfil actualizado');
        return { success: true, user: response.user };
      } else {
        throw new Error(response.message || 'Error actualizando perfil');
      }
    } catch (error) {
      console.error('‚ùå Error actualizando perfil:', error);
      setError(error);
      return { 
        success: false, 
        error: error.message || 'Error actualizando perfil' 
      };
    }
  }, []);

  // Funci√≥n para actualizar configuraciones
  const updateSettings = useCallback(async (settings) => {
    try {
      setError(null);
      const response = await apiClient.updateSettings(settings);

      if (response.success) {
        // Actualizar settings en el usuario actual
        setUser(prevUser => ({
          ...prevUser,
          settings: response.settings
        }));
        console.log('‚öôÔ∏è Configuraciones actualizadas');
        return { success: true, settings: response.settings };
      } else {
        throw new Error(response.message || 'Error actualizando configuraciones');
      }
    } catch (error) {
      console.error('‚ùå Error actualizando configuraciones:', error);
      setError(error);
      return { 
        success: false, 
        error: error.message || 'Error actualizando configuraciones' 
      };
    }
  }, []);

  // Funci√≥n para cambiar contrase√±a
  const changePassword = useCallback(async (currentPassword, newPassword) => {
    try {
      setError(null);
      const response = await apiClient.changePassword(currentPassword, newPassword);

      if (response.success) {
        console.log('üîê Contrase√±a cambiada exitosamente');
        // Despu√©s de cambiar contrase√±a, el backend revoca todos los tokens
        // Por lo que debemos limpiar el estado y redirigir al login
        apiClient.clearTokens();
        setUser(null);
        return { 
          success: true, 
          message: 'Contrase√±a cambiada. Inicia sesi√≥n nuevamente.' 
        };
      } else {
        throw new Error(response.message || 'Error cambiando contrase√±a');
      }
    } catch (error) {
      console.error('‚ùå Error cambiando contrase√±a:', error);
      setError(error);
      return { 
        success: false, 
        error: error.message || 'Error cambiando contrase√±a' 
      };
    }
  }, []);

  // Funci√≥n para subir foto de perfil
  const uploadProfilePicture = useCallback(async (file) => {
    try {
      setError(null);
      const response = await apiClient.uploadProfilePicture(file);

      if (response.success) {
        // Actualizar foto de perfil en el usuario actual
        setUser(prevUser => ({
          ...prevUser,
          profilePhoto: response.profilePhoto
        }));
        console.log('üì∑ Foto de perfil actualizada');
        return { success: true, profilePhoto: response.profilePhoto };
      } else {
        throw new Error(response.message || 'Error subiendo foto');
      }
    } catch (error) {
      console.error('‚ùå Error subiendo foto de perfil:', error);
      setError(error);
      return { 
        success: false, 
        error: error.message || 'Error subiendo foto de perfil' 
      };
    }
  }, []);

  // Funci√≥n para eliminar foto de perfil
  const deleteProfilePicture = useCallback(async () => {
    try {
      setError(null);
      const response = await apiClient.deleteProfilePicture();

      if (response.success) {
        // Limpiar foto de perfil del usuario actual
        setUser(prevUser => ({
          ...prevUser,
          profilePhoto: { url: null, publicId: null }
        }));
        console.log('üóëÔ∏è Foto de perfil eliminada');
        return { success: true };
      } else {
        throw new Error(response.message || 'Error eliminando foto');
      }
    } catch (error) {
      console.error('‚ùå Error eliminando foto de perfil:', error);
      setError(error);
      return { 
        success: false, 
        error: error.message || 'Error eliminando foto de perfil' 
      };
    }
  }, []);

  // Funci√≥n para obtener estad√≠sticas del usuario
  const getUserStats = useCallback(async () => {
    try {
      setError(null);
      const response = await apiClient.getUserStats();

      if (response.success) {
        return { success: true, stats: response.stats };
      } else {
        throw new Error(response.message || 'Error obteniendo estad√≠sticas');
      }
    } catch (error) {
      console.error('‚ùå Error obteniendo estad√≠sticas:', error);
      setError(error);
      return { 
        success: false, 
        error: error.message || 'Error obteniendo estad√≠sticas' 
      };
    }
  }, []);

  // Funci√≥n para refrescar datos del usuario
  const refreshUser = useCallback(async () => {
    try {
      if (!apiClient.isAuthenticated()) {
        setUser(null);
        return { success: false, error: 'No autenticado' };
      }

      const response = await apiClient.getProfile();

      if (response.success) {
        setUser(response.user);
        return { success: true, user: response.user };
      } else {
        throw new Error(response.message || 'Error refrescando usuario');
      }
    } catch (error) {
      console.error('‚ùå Error refrescando usuario:', error);
      setError(error);
      return { 
        success: false, 
        error: error.message || 'Error refrescando datos' 
      };
    }
  }, []);

  // Limpiar errores
  const clearError = useCallback(() => {
    setError(null);
  }, []);

  // Valor del contexto
  const value = {
    // Estado
    user,
    loading,
    error,

    // M√©todos de autenticaci√≥n
    register,
    login,
    logout,

    // M√©todos de perfil
    updateProfile,
    updateSettings,
    changePassword,
    uploadProfilePicture,
    deleteProfilePicture,

    // M√©todos utilitarios
    getUserStats,
    refreshUser,
    clearError,

    // Estado de autenticaci√≥n
    isAuthenticated: !!user && !loading,
    canUseP2P: user?.verification?.phone && user?.verification?.identity && !user?.suspension?.suspended,

    // Informaci√≥n del usuario
    currentUser: user,
    userRole: user?.role || 'user',
  };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
}

// Hook personalizado para usar el contexto
export function useAuth() {
  const context = useContext(AuthContext);
  if (context === undefined) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
}

// Hook para verificar si est√° autenticado (m√°s simple)
export function useAuthState() {
  const { user, loading } = useAuth();
  return { user, loading, isAuthenticated: !!user && !loading };
}

export default AuthContext;
